# K means attemptsimport matplotlib.pyplot as pltimport pandas as pdimport sklearn.cluster as skcimport numpy as npimport csvfrom mpl_toolkits.mplot3d import Axes3D# K means attemptf=open('output/kmeans80.csv', mode="rt", encoding="utf8")kmeans1 =np.loadtxt(f, dtype=None, delimiter=',',unpack=True)df2 = pd.DataFrame({    'x': kmeans1[0,:]*100,    'y': kmeans1[1,:]*100,    'z': kmeans1[2,:]*100})print("Original Scatter - No Clustering")plt.ylim(np.min(df2['y']),np.max(df2['y']))plt.scatter(df2['x'],df2['y'])plt.show()kmeans = skc.KMeans(n_clusters=5)kmeans.fit(df2)labels = kmeans.predict(df2)centroids = kmeans.cluster_centers_print(kmeans.labels_)#plt.ylim(np.min(df2['y']),np.max(df2['y']))#plt.scatter(df2['x'],df2['y'])#for i in range(len(centroids)):#    plt.scatter(centroids[i,0],centroids[i,1])#plt.show()index=np.argsort(labels)labels=labels[index]print(labels)df2['x']=df2['x'][index]df2['y']=df2['y'][index]df2['z']=df2['z'][index]fig = plt.figure()ax = Axes3D(fig)#plt.ylim(np.min(df2['y']),np.max(df2['y']))#plt.xlim(np.min(df2['x']),np.max(df2['x']))Colors = ['turquoise','red','green','blue','fuchsia','gold','darkred','purple','gray','darkgreen','pink','brown','cyan','darkblue','yellow']j = 0for i in range(len(labels)):    if(labels[i] != labels[i-1] and i != 0 ):        # Change the color        print("Changed")        j = j + 1    # Do Something    ax.scatter(df2['z'][i],df2['x'][i],df2['y'][i],color=Colors[j],s=50, cmap='viridis')for i in range(len(centroids)):    ax.scatter(centroids[i,2],centroids[i,0],centroids[i,1],s=50,marker='*',color='black')plt.show()